<style>
	* {
		margin: 0;
		padding: 0;
		box-sizing: border-box;
	}

	/* utility */
	.flex {
		display: flex;
	}

	button {
		padding: 5px 2em;
		font-size: 1rem;
	}

	/* hero start */

	.home {
		height: 90vh;
		min-height: 600px;
		background: url('../landing.png');
		background-size: 100% 100%;
		background-repeat: no-repeat;
		color: white;
		font-family: 'Josefin Sans', sans-serif;
		display: flex;
		flex-direction: column;
		gap: 15vh;

		padding: 1em 3em;
	}

	.home::before {
		content: '';
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		background: linear-gradient(180deg, hsla(260, 87%, 9%, 0.6), hsla(260, 87%, 9%, 0.2));
		z-index: 1;
	}

	.home * {
		position: relative;
		z-index: 2;
	}

	nav {
		align-items: center;
		gap: 4em;
	}

	nav > a {
		align-items: center;
		text-decoration: none;
		gap: 0.4em;
	}

	a > p {
		font-size: 1.5em;
		text-decoration: none;
		color: white;
	}

	ul {
		gap: 2.3em;
		list-style: none;
		color: #a6a6a6;
	}

	ul > li:hover {
		color: aliceblue;
		text-decoration: underline;
		transform: scale(115%);
	}

	@media (max-width: 760px) {
		ul > li {
			display: none;
		}
	}

	nav > section {
		width: 83%;
		justify-content: space-between;
		align-items: center;
	}

	nav > section > div {
		gap: 1em;
		justify-content: space-between;
		align-items: center;
	}

	button {
		width: fit-content;
		padding: 0.4em 1em;
		border: none;
		border-radius: 0.3em;
		background-color: #185fb2;
		color: aliceblue;
		letter-spacing: 0.3em;
		font-family: 'Racing Sans One', sans-serif;
	}

	/* nav bar style end */

	/* hero section begin */

	h1 {
		font-family: 'Racing Sans One', sans-serif;
		font-size: clamp(2em, 6vh, 6em);
		width: 75%;
		letter-spacing: 0.1em;
		text-shadow: -10px -10px -2px rgba(0, 0, 0, 0.5);
		text-overflow: ellipsis;
	}

	h1 > span {
		color: #f5db51;
	}

	h1 + p {
		width: 65%;
		font-size: larger;
		color: #a6a6a6;
		line-height: 1.5em;
		margin-top: 1.3em;
	}

	/* hero ends */

	/* cta begins */
	.home > section:last-of-type {
		width: 90%;
		height: 13vh;
		margin: 0 auto;
		anchor-name: --cta;
	}

	@media (max-width: 600px) {
		.home > section:last-of-type {
			display: none;
		}
	}

	.home > section:last-of-type > ul {
		width: fit-content;
		display: flex;
		font-weight: bold;
		z-index: -100;
		gap: 0;
	}

	.home > section:last-of-type > ul > li:first-of-type {
		background-color: #185fb2;
		padding: 1em 3em;
		color: white;
		border-radius: 10px 10px 0 0;
	}

	.home > section:last-of-type > ul > li:last-of-type {
		background-color: #a6a6a660;
		padding: 1em 3em;
		color: rgb(34, 34, 34);
		border-radius: 10px 10px 0 0;
	}

	.home > section:last-of-type > div {
		display: flex;
		height: 100%;
		justify-content: space-around;
		align-items: center;
		background-color: #b5d2f29a;
		border-radius: 1.3em;
	}

	.home > section:last-of-type > div > article > section > h1 {
		color: black;
		font-size: 1rem;
	}

	article > section > p {
		color: black;
		font-size: 0.8rem;
		text-align: center;
	}
</style>
